import React from 'react'

const App = () => {
    const course = 'Half Stack application development'
    const parts = [
        {
            name: 'Fundamentals of React',
            exercises: 10
        },
        {
            name: 'Using props to pass data',
            exercises: 7
        },
        {
            name: 'State of a component',
            exercises: 14
        }
    ]

    let total = 0

    for (const part of parts) {
        total += part.exercises
    }

    console.log(total)

    return (
      <>
          <Header course={course} />
          <Content parts={parts} />
          <Total sum={total} />
      </>
  )
}

const Header = (props) => {
    return (
        <h1>{props.course}</h1>
    )
}

const Content = (props) => {
    return (
        <>
            <Part  />
            <Part  />
            <Part />
        </>
    )
}

const Total = (props) => {
    return (
        <p>Number of exercises </p>
    )
}

const Part = (props) => {
    return (
        <p></p>
    )
}

export default App
